sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
      return;
   }
   //Grab needed parameters: quantity needed, item cost
	var qty = Number(newValue.split(',').join(''));
	var cost = g_form.getValue('item_cost');
	var total = qty * cost;

	g_form.clearValue('total_cost');
	g_form.setValue('total_cost', total, '$' + total.toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&,'));
}
