sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
   //Access ShareRecords via Glide Ajax
	var getRecords = new GlideAjax('ShareRecords');
	// Specify the getItemCost method
	getRecords.addParam('sysparm_name', 'getItemCost');
	// Pass the item_needed variable as sysparm_item
	getRecords.addParam('sysparm_item', g_form.getValue('item_needed'));
	// Send the request to the server
    getRecords.getXML(setItemCost);

	// When the response is back from the server
	function setItemCost(response) {
		// Extract the answer from the response
		var serverItemCost = response.responseXML.documentElement.getAttribute('answer');
		// Clear the item cost value
		g_form.clearValue('item_cost');
		// Set the new item cost value
		g_form.setValue('item_cost', serverItemCost);
		// Update the total cost field

		var qty = Number(g_form.getValue('quantity_needed').split(',').join(''));
		var total = qty * serverItemCost;

		g_form.clearValue('total_cost');
		g_form.setValue('total_cost', total, '$' + total.toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&,'));
	}
}
