sys_script_client.script:
function onLoad() {
	if (g_form.isNewRecord() === false) {
		return;
	}
   //Access the ShareRecords Script Include via GlideAjax
	var getRecords = new GlideAjax('ShareRecords');

	getRecords.addParam('sysparm_name', 'getProjectNames');
	getRecords.addParam('sysparm_project', g_form.getValue('project'));
	getRecords.getXML(getProjectsArr);

	function getProjectsArr(response) {
		var answerFromXML = response.responseXML.documentElement.getAttribute('answer');
		// test logs
		// g_form.addInfoMessage(answerFromXML);
		// g_form.addInfoMessage(typeof answerFromXML);
		// Decode JSON
		var answer = JSON.parse(answerFromXML);

		// test log
		// g_form.addInfoMessage(answer);

		//Populate choice field
		g_form.clearOptions('project');
		answer.forEach(function (proj) {
			g_form.addOption('project', proj.toLowerCase().split(' ').join('-'), proj);
		});
	}
}
