sys_script_include.script:
var ShareRecords = Class.create();
ShareRecords.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	// Grab all records from the Items list
	getItemIDs: function() {
		var items = new GlideRecord('x_417674_materials_items');
		items.get(this.getParameter('sysparm_item_needed'));
		// Run Query
		items.addQuery();
		items.query();

		// Create array of Item IDs
		// Initialize array
		var itemsArr = [];
		// Populate Array with each Item ID
		while(items.next()) {
			var newItem = items.getValue('item_id');
			itemsArr.push(newItem);
		}
		// Store Array in JSON formatted string.
		var data = JSON.stringify(itemsArr);

		return data;

	},

	getProjectNames: function() {
		var projects = new GlideRecord('x_417674_materials_projects');
		projects.get(this.getParameter('sysparm_project'));
		// Run Query
		projects.addQuery();
		projects.query();

		// Create array of Project IDs:
		// Initialize array
		var projectsArr = [];
		// Populate Array with each Project ID
		while(projects.next()) {
			var newProject = projects.getValue('project_name');
			projectsArr.push(newProject);
		}
		// Store Array in JSON formatted string.
		var data = JSON.stringify(projectsArr);

		return data;
	},

    type: 'ShareRecords'
});
